/* APPLIES TO THE WHOLE CARD */
.card {
    font-family: Menlo, baskerville, sans;
    font-size: 18px; /* FONT SIZE */
    text-align: center; /* ALIGN TEXT */
    color: #D7DEE9; /* FONT COLOR */
    line-height: 1.6em;
    background-color: #333B45; /* BACKGROUND COLOR */
}

/* OTRO ESTILO DE TARJETAS
.card {
    font-family: arial;
    font-size: 20px;
    text-align: center;
    color: #586e75;
    background-color: #fdf6e3;
}
*/

/* IMAGE CSS CODE */

img {
/width: auto;
/height: auto;
max-width: 1200px;
max-height: 110vh;
border-radius: 5%;
border: 5px solid #ddd;
}


/* TAG CSS CODE*/

.tags{
//font-family: Menlo, baskerville, adobe caslon pro, sans;
font-family:Helvetica;
font-size: 14px;
//font-weight: bold;
text-align: center;
color: #595959;
/text-transform: uppercase; 
}

.new-tag {
    background-color: #03A9F4;
    border: none;
    color: black;
   padding:.25rem; 
/    padding: 10px 20px;
    text-align: center;
    text-decoration: none;
    display: inline-block;
/  margin: 4px 2px;
    margin:.25rem;
/    cursor: pointer;
    border-radius: 3px;
   font-weight: bold
}

/* COLOR ACCENTS FOR BOLD-ITALICS-UNDERLINE */
b { color: #C695C6;   font-style: bold;} /* BOLD STYLE */
i  { color: IndianRed;   font-style: italic;} /* ITALICS STYLE */

tmpls.qfmt:
{{#Tags}}
<div class=tags>{{Tags}}</div>
{{/Tags}}
<script>
    var tagEl = document.querySelector('.tags');
    var tags = tagEl.innerHTML.split(' ');
    var html = '';
    tags.forEach(function(tag) {
        var newTag = '<span class="new-tag">' + tag + '</span>';
        html += newTag;
    });
    tagEl.innerHTML = html;
</script>

{{Front}}

